import React from "react"
import './Home.css'
import {people as data}   from '../../utils/data'
import {useState,useEffect} from  'react'
import { useHistory } from "react-router-dom";


function Home(){

    const [searchInput,setSearchInput] = useState('')
    const [people,setPeople] = useState(data)
    const [filteredPeople,setFilteredPeople] = useState(people)
    
    const history = useHistory();

    useEffect(()=>{
        fetch("http://localhost:5000/User",)
        .then(response => response.json())
        .then(data =>{ 
            debugger
            setPeople(data)
            console.log(data)});
    },[])

    


    useEffect(()=>{
        const newFilteredPeople = people.filter((item) =>
      ((item.firstname+' '+item.lastname).toLowerCase().includes(searchInput.toLocaleLowerCase())))        
        setFilteredPeople(newFilteredPeople)       
    },[people,searchInput])


    const handleSearchChange = (e)=>{     
        setSearchInput(e.target.value)       
    }

    const handleClick = (event) => {
        event.preventDefault();   
        
        //after submit form redirect user
        history.push('/AddUser');
        
      };

    return (
        <div>
        <div className="header-margin"></div>
        <div className="title"><h1>Birthday App</h1></div>
    <div>
    <input 
    className={"form-control form-control-lg search-main"} 
    type={"search"} 
    placeholder={"Search"}
    value={searchInput}
    onChange={handleSearchChange}>
    
    </input>
    </div>
    
    <div className={"btn-search"}>
    <button type={"button"} className={"btn btn-secondary"} onClick={handleClick} >Add</button>
    </div>

    


    <div className="card-list">
        {filteredPeople.map((person)=> (
            <div> <h2>{(person.firstname+' '+person.lastname)}</h2>
             <h2>{person.birthday}</h2>
             </div>))}
    </div>
        </div>
    )
}


export default Home;
