import {useState} from 'react'
import { useHistory } from "react-router-dom";
import { useEffect } from "react";
import { useLocation } from "react-router-dom";


export const EditUser = () => {
    const history = useHistory();
    const location = useLocation();

const [newUser,setNewUser] = useState({
    firstname:'',
    lastname:'',
    birthday:'',
    group:'',
    reminder:''
});

const handleSubmit = (event) => {

    
    event.preventDefault();
    const {firstname,lastname,birthday,group,reminder} = newUser;
    fetch("http://localhost:5000/User",{
        method: 'put',
        headers: {'Content-Type':'application/json'},
        body: 
            JSON.stringify(newUser)
    // "firstname": firstname,
    // "lastname": lastname,
    // "group": group,
    // "birthday":birthday
     
    })
    //after submit form redirect user

    event.preventDefault();       
    //after submit form redirect user
    
    
    console.log(newUser)
    history.push('/');
   
    
  };

const handleFormChange = (e)=>{
const currentUser = newUser
const key = e.target.name
const value = e.target.value
currentUser[key]=value
setNewUser(currentUser)
}


  return (
    <div>
    <div className='form-add'>

    
    <div className='form-add-header'>Edit Contact</div>
    <div>
        <form  onSubmit={handleSubmit}>
            <div className=''>
                <label>First Name</label>
                <input name={"firstname"}
                    className={'input-txt'} 
                    type={'text'}
                    placeholder={'First Name'}
                    onChange={handleFormChange}
                    value={location.state.user.firstname}>
                </input>
            </div>    

            <div className=''>
                <label>Last Name</label>
                <input 
                    name={"lastname"}
                    className={'input-txt'} 
                    type={'text'}
                    placeholder={'Last Name'}
                    onChange={handleFormChange}>
                </input>
            </div>

            <div className='select'>
                <div>
                <label className=''>Group</label>
                </div>
                
                <select 
                name={"group"}
                class="form-control select" 
                id="drp-group"
                onChange={handleFormChange}>
                    
                    <option value='family'>Family</option>
                    <option value='friend'>Friend</option>
                    <option value='work'>Work</option>
                </select>
            </div>

            <div className=''>
                <label>Birthday</label>
                <input 
                    name={'birthday'}
                    className={'input-txt'} 
                    type={'date'}
                    onChange={handleFormChange}>
                </input>
            </div>

            <div className=''>
                <label>Remind Me Days Before</label>
                <input 
                    name={"reminder"}
                    className={'input-txt'} 
                    type={'text'}
                    placeholder={'Days before'}
                    onChange={handleFormChange}>
                </input>
            </div>
            <div>
                <button id='btn-adduser' className="btn btn-secondary" type='submit'>Add User</button>
            </div>
        </form>
    </div>
 
   
    </div>
    </div>
  )
}
