import React from "react"
import './Home.css'
import {people as data}   from '../../utils/data'
import {useState} from  'react'
import { useEffect } from "react/cjs/react.production.min"


function Home(){

    const [searchInput,setSearchInput] = useState('')
    const [people,setPeople] = useState(data)
    const [filteredPeople,setFilteredPeople] = useState(people)



    useEffect(()=>{
        const newFilteredPeople = people.filter((item) =>
      (item.name.toLowerCase().includes(searchInput)))        
        setFilteredPeople(newFilteredPeople)       
    },[people,searchInput])


    const handleSearchChange = (e)=>{     
        setSearchInput(e.target.value.toLowerCase())       
    }

    return (
        <div>
        <div className="title"><h1>Birthday App</h1></div>
    <div>
    <input 
    className={"form-control form-control-lg search-main"} 
    type={"text"} 
    placeholder={"Friend Name"}
    value={searchInput}
    onChange={handleSearchChange}>
    
    </input>
    </div>
    
    <div className={"btn-search"}>
    <button type={"button"} className={"btn btn-secondary"}>Add</button>
    </div>


    <div className="card-list">
        {filteredPeople.map((person)=> (<div> <h2>{person.name}</h2> <h2>{person.birthday}</h2></div>))}
    </div>
        </div>
    )
}


export default Home;
